    border-radius: 100px;
     object-fit: 'cover';
  }

  .track {
    width: 250px;
    height: 250px;
    position: relative;
    display: inline-block;
    margin:20px;
    cursor: pointer;
  }

  .track-image {
    width: 250px;
    height: 250px;
  }

  .track-text {
    position: absolute;
    width: 250px;
    bottom: 0px;
    background-color: black;
    color: white;
    padding: 5px;
  }

  .track-icon {
    position: absolute;
    bottom: 100px;
    left: 100px;
    opacity: 0.8;
    color: white;
    background-color: black;
    width: 50px;
    height: 50px;
    border-radius: 25px;
